    local lines = get_lines(fName)
    local lOut = {"devtmpfs", "tmpfs"}
    local tDev = {}
    local ret = {}
    for _, line in ipairs(lines) do
        for _, t in ipairs(lOut) do
            if pystring:startswith(line, t) then
                insert_point(ret, line)
                goto continue
